import React from 'react';

export default function PostMessage({message, handleFormEdits, handleFormPost}) {
    function updateFormEdits(e) {
        handleFormEdits(e)
    }

    function postFormUpdate(e) {
        e.preventDefault();
        handleFormPost();
    }
    return(
        <div className="div__post-message">
            <form onSubmit={postFormUpdate}>
                <input
                    name="messageText"
                    value={message.messageText}
                    onChange={updateFormEdits}
                    placeholder="Enter Message Here"  
                />
                <input 
                    type="submit"
                    value="Submit"
                />
            </form>
        </div>
    )
}
